property.code:
define('entra-sync-notification', {
  prefix: 'entra.sync.notification.',
  keys: [
    'inprogress',
    'done',
    'error',
    'noAction'
  ]
});

require(['jquery', 'xwiki-meta', 'xwiki-job-runner', 'xwiki-l10n!entra-sync-notification'],
        function($, xwikiMeta, JobRunner, l10n) {
  $(document).on('click', '.entra-sync-action', function (event) {
    event.preventDefault();
    const form = $(this).closest('form');
    const disable = String(form.find('input[name="disable"]').prop('checked'));
    const remove   = String(form.find('input[name="remove"]').prop('checked'));
    if (disable === 'false' && remove === 'false') {
      const notification = new XWiki.widgets.Notification(l10n.get('noAction'), 'info');
    } else {
      $(this).prop('disabled', true);
      const data = {
        disable: disable,
        remove: remove
      };
      const notification = new XWiki.widgets.Notification(l10n.get('inprogress'), 'inprogress');
      $.ajax({
        url: form.attr('action'),
        type: 'PUT',
        data: $.param(data),
        success: function (response) {
          checkSyncJob(disable, remove, notification)
        },
        error: function (xhr) {
          console.error('Error:', xhr);
          notification.replace(new XWiki.widgets.Notification(l10n.get('error'),'error'));
          $(this).prop('disabled', false);
        }
      });
    }
  });

  const checkSyncJob = function(disable, remove, notification) {
    let documentReference = XWiki.Model.resolve('EntraID.Code.SyncJobResource', XWiki.EntityType.DOCUMENT);
    var targetURL = new XWiki.Document(documentReference).getURL('get');
    var targetJobId = ['entra', 'users', 'sync', disable, remove];
    var data = [];
    data.push(
      {name: 'outputSyntax', value: 'plain'},
      {name: 'sheet', value: 'EntraID.Code.SyncJobResource'},
      {name: 'action', value: 'jobStatus'},
      {name: 'jobId', value: targetJobId.join('/')},
      {name: 'data', value: 'jobStatus'},
      {name: 'form_token', value: xwikiMeta.form_token}
    );
    return Promise.resolve(new JobRunner({
      createStatusRequest: function(jobId) {
        return {
          url: targetURL,
          data: {
            outputSyntax: 'plain',
            sheet: 'EntraID.Code.SyncJobResource',
            data: 'jobStatus',
            jobId: jobId.join('/')
          }
        };
      }
    }).run(targetURL, data)).then((response) => {
      if (response.error) {
        throw new Error(response.error.message);
      } else {
        notification.replace(new XWiki.widgets.Notification(l10n.get('done'),'done'));
      }
    }).catch((reason) => {
      notification.replace(new XWiki.widgets.Notification(l10n.get('error'),'error'));
      return Promise.reject(reason);
    }).finally(() => {
      $('.entra-sync-action').prop('disabled', false);
    });
  };
});
